// src/components/GameBoard.jsx
import React, { useEffect, useState } from "react";
import socket from "../socket";

const ROWS = 6;
const COLS = 7;

const GameBoard = ({ username, gameId, setInGame }) => {
  const [board, setBoard] = useState(Array.from({ length: ROWS }, () => Array(COLS).fill(null)));
  const [turn, setTurn] = useState(null);
  const [opponent, setOpponent] = useState(null);

  const makeMove = (col) => {
    if (turn !== username) return;
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    console.log("🔁 useEffect running. Username:", username);

    socket.on("connect", () => console.log("✅ Socket connected:", socket.id));

    socket.on("gameStarted", ({ board, turn, players }) => {
      console.log("🎮 Received gameStarted:", { board, turn, players });
      setBoard(board);
      setTurn(turn);

      const opp = players.find((p) => p !== username);
      console.log("👤 Opponent identified as:", opp);
      setOpponent(opp || "BotMaster");
    });

    socket.on("opponentFound", ({ opponent }) => {
      console.log("👥 Opponent found event:", opponent);
      setOpponent(opponent);
    });

    socket.on("moveMade", ({ board }) => {
      setBoard([...board]);
    });

    socket.on("turnChange", ({ turn }) => {
      setTurn(turn);
    });

    socket.on("gameOver", ({ winner, draw }) => {
      console.log("🏁 Game over. Winner:", winner, "Draw:", draw);
      if (draw) {
        alert("It's a Draw!");
      } else {
        alert(winner === username ? "You Win!" : `${winner} Wins`);
      }
      setInGame(false);
    });

    socket.on("playerDisconnected", ({ message }) => alert(message));
    socket.on("playerRejoined", ({ message }) => alert(message));

    return () => {
      socket.off("gameStarted");
      socket.off("moveMade");
      socket.off("turnChange");
      socket.off("gameOver");
      socket.off("opponentFound");
      socket.off("playerDisconnected");
      socket.off("playerRejoined");
    };
  }, [username, setInGame]);


  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>4 in a Row</h2>
      <h3>Turn: {turn || "Loading..."}</h3>
      <h4>Opponent: {opponent || "Waiting..."}</h4>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${COLS}, 50px)`,
          justifyContent: "center",
          gap: "5px",
        }}
      >
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`}
              onClick={() => makeMove(cIdx)}
              style={{
                width: 50,
                height: 50,
                backgroundColor:
                  cell === "X" ? "red" : cell === "O" ? "yellow" : "#eee",
                border: "1px solid #333",
                borderRadius: "50%",
                cursor: turn === username ? "pointer" : "not-allowed",
              }}
            ></div>
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
