import React, { useEffect, useState } from "react";
import socket from "../socket";
import { useNavigate } from "react-router-dom";

const ROWS = 6;
const COLS = 7;

const GameBoard = ({ username, gameId, setInGame }) => {
  const navigate = useNavigate();
  const [board, setBoard] = useState(Array.from({ length: ROWS }, () => Array(COLS).fill(null)));
  const [turn, setTurn] = useState(null);
  const [opponent, setOpponent] = useState("Waiting...");

  const makeMove = (col) => {
    if (turn !== username) return;
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    // Fetch initial game state
    socket.emit("getGameState", { gameId });

    socket.on("gameState", ({ board, turn, players }) => {
      setBoard(board);
      setTurn(turn);
      const opp = players.find(p => p !== username);
      setOpponent(opp || "BotMaster");
    });

    socket.on("moveMade", ({ board }) => {
      setBoard([...board]);
      setTurn((prev) => (prev === username ? opponent : username));
    });

    socket.on("gameOver", ({ winner, draw }) => {
      setTimeout(() => {
        if (draw) {
          alert("ü§ù It's a Draw!");
        } else {
          alert(winner === username ? "üèÜ You Win!" : `üòû ${winner} Wins`);
        }
        setInGame(false);
        navigate("/");
      }, 300);
    });

    socket.on("playerDisconnected", ({ message }) => alert(message));
    socket.on("playerRejoined", ({ message }) => alert(message));

    return () => {
      socket.off("gameState");
      socket.off("moveMade");
      socket.off("gameOver");
      socket.off("playerDisconnected");
      socket.off("playerRejoined");
    };
  }, [gameId, username, opponent, navigate, setInGame]);

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#0b1d1f",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "40px 20px",
        color: "#ffffff",
      }}
    >
      <h1 style={{ fontSize: "32px", marginBottom: "10px" }}>Connect4</h1>
      <h3 style={{ fontSize: "18px", marginBottom: "5px" }}>
        Your Turn:{" "}
        <span style={{ color: "#6ee7b7", fontWeight: "bold" }}>
          {turn === username ? "‚úÖ Your Move" : "‚è≥ Waiting"}
        </span>
      </h3>
      <h4 style={{ fontSize: "16px", marginBottom: "20px" }}>
        Playing against:{" "}
        <span style={{ color: "#6ee7b7" }}>{opponent}</span>
      </h4>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${COLS}, 60px)`,
          justifyContent: "center",
          gap: "6px",
          backgroundColor: "#122c2f",
          padding: "20px",
          borderRadius: "12px",
        }}
      >
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`}
              onClick={() => makeMove(cIdx)}
              style={{
                width: "60px",
                height: "60px",
                backgroundColor:
                  cell === "X" ? "#d62828" : cell === "O" ? "#fcbf49" : "#1e3d3f",
                borderRadius: "50%",
                cursor: turn === username ? "pointer" : "not-allowed",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                transition: "transform 0.2s",
              }}
              onMouseOver={(e) => {
                if (turn === username) e.currentTarget.style.transform = "scale(1.05)";
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.transform = "scale(1)";
              }}
            >
              {cell && (
                <span style={{ fontSize: "24px", color: "#fff", fontWeight: "bold" }}>
                  {cell}
                </span>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
