// src/components/Leaderboard.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";

const Leaderboard = () => {
  const [data, setData] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const res = await axios.get("https://connect4-backend-ka4c.onrender.com/leaderboard");
        setData(res.data);
      } catch (err) {
        console.error("Error fetching leaderboard:", err.message);
        setError("Unable to load leaderboard.");
      }
    };

    fetchLeaderboard();
  }, []);

  return (
    <div
      style={{
        maxWidth: "100%",
        margin: "0 auto",
        backgroundColor: "transparent",
        padding: "20px",
        borderRadius: "12px",
        textAlign: "center",
      }}
    >
      {error ? (
        <p style={{ color: "#ff6b6b", fontSize: "16px" }}>{error}</p>
      ) : data.length === 0 ? (
        <p style={{ color: "#ccc", fontSize: "16px" }}>No data yet. Play to enter the leaderboard!</p>
      ) : (
        <ol style={{ 
          textAlign: "left", 
          padding: "0 10px", 
          listStylePosition: "inside",
          maxHeight: "300px",
          overflowY: "auto"
        }}>
          {data.slice(0, 5).map((user, i) => (              <li
                key={i}
                style={{
                  padding: "12px 16px",
                  backgroundColor: i === 0 ? "#6ee7b7" : "#0b1d1f",
                  borderRadius: "8px",
                  marginBottom: "8px",
                  fontWeight: i === 0 ? "bold" : "normal",
                  boxShadow: "0 2px 4px rgba(0, 0, 0, 0.2)",
                  color: i === 0 ? "#000" : "#fff",
                  border: `1px solid ${i === 0 ? "#6ee7b7" : "#333"}`,
                }}
              >
                {user.winner} â€”{" "}
                <strong>{user.wins}</strong> win{user.wins > 1 ? "s" : ""}
              </li>
          ))}
        </ol>
      )}
    </div>
  );
};

export default Leaderboard;
