// GameBoard.jsx

import React, { useEffect, useState } from "react";
import socket from "../socket";
import { useNavigate } from "react-router-dom";

const ROWS = 6;
const COLS = 7;

const GameBoard = ({
  username,
  gameId,
  board: initialBoard,
  turnProp,
  opponentProp,
  setInGame,
}) => {
  const navigate = useNavigate();
  const [board, setBoard] = useState(
    initialBoard || Array.from({ length: ROWS }, () => Array(COLS).fill(null))
  );
  const [turn, setTurn] = useState(turnProp || username);
  const [opponent, setOpponent] = useState(opponentProp || "BotMaster");
  const [symbol, setSymbol] = useState("X");

  useEffect(() => {
    // Determine your symbol based on who starts
    if (turnProp === username) {
      setSymbol("X");
    } else {
      setSymbol("O");
    }
  }, [turnProp, username]);

  const makeMove = (col) => {
    if (!gameId || turn !== username) return; // Not your turn
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    const handleMoveMade = ({ board: updatedBoard }) => {
      setBoard([...updatedBoard]);
      setTurn((prevTurn) => (prevTurn === username ? opponent : username));
    };

    const handleGameOver = ({ winner, draw }) => {
      setTimeout(() => {
        if (draw) {
          alert("ü§ù It's a draw!");
        } else if (winner === username) {
          alert("üèÜ You win!");
        } else {
          alert(`üòû ${winner} wins.`);
        }
        setInGame(false);
        navigate("/");
      }, 500);
    };

    socket.on("moveMade", handleMoveMade);
    socket.on("gameOver", handleGameOver);

    return () => {
      socket.off("moveMade", handleMoveMade);
      socket.off("gameOver", handleGameOver);
    };
  }, [username, opponent, navigate, setInGame]);

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#0b1d1f",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "40px 20px",
        color: "#ffffff",
      }}
    >
      <h1 style={{ fontSize: "32px", marginBottom: "10px" }}>Connect4</h1>
      <h3 style={{ fontSize: "18px", marginBottom: "5px" }}>
        Your Turn:{" "}
        <span style={{ color: "#6ee7b7", fontWeight: "bold" }}>
          {turn === username ? "‚úÖ Your Move" : "‚è≥ Waiting"}
        </span>
      </h3>
      <h4 style={{ fontSize: "16px", marginBottom: "20px" }}>
        Playing against: <span style={{ color: "#6ee7b7" }}>{opponent}</span>
      </h4>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${COLS}, 60px)`,
          gap: "6px",
          backgroundColor: "#122c2f",
          padding: "20px",
          borderRadius: "12px",
        }}
      >
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`}
              onClick={() => makeMove(cIdx)}
              style={{
                width: "60px",
                height: "60px",
                backgroundColor:
                  cell === "X"
                    ? "#d62828" // red for player 1
                    : cell === "O"
                    ? "#fcbf49" // yellow for player 2
                    : "#1e3d3f", // empty
                borderRadius: "50%",
                cursor: turn === username ? "pointer" : "not-allowed",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                transition: "background 0.3s",
              }}
            >
              {/* No text inside disc, purely color-based */}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
