import React, { useState, useEffect } from "react";
import Lobby from "./components/Lobby";
import GameBoard from "./components/GameBoard";
import Leaderboard from "./components/Leaderboard";
import socket from "./socket";

function App() {
  const [username, setUsername] = useState("");
  const [gameId, setGameId] = useState("");
  const [inGame, setInGame] = useState(false);
  const [turnProp, setTurnProp] = useState("");
  const [opponentProp, setOpponentProp] = useState("");

  useEffect(() => {
    const handleGameStarted = ({ gameId }) => {
      console.log("ðŸŽ® Received gameStarted in App:", { gameId });
      setGameId(gameId);
      setInGame(true);

      // Since your backend does not send turn or players:
      setTurnProp(username); // you start first by default
      setOpponentProp("BotMaster"); // assume bot unless you add dynamic later
    };

    socket.on("gameStarted", handleGameStarted);

    return () => {
      socket.off("gameStarted", handleGameStarted);
    };
  }, [username]);

  return (
    <div>
      {!inGame ? (
        <>
          <Lobby
            username={username}
            setUsername={setUsername}
            setGameId={setGameId}
            setInGame={setInGame}
          />
          <Leaderboard />
        </>
      ) : (
        <GameBoard
          username={username}
          gameId={gameId}
          turnProp={turnProp}
          opponentProp={opponentProp}
          setInGame={setInGame}
        />
      )}
    </div>
  );
}

export default App;
