// src/components/Lobby.jsx

import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import socket from "../socket";

const Lobby = () => {
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const joinGame = () => {
    const trimmed = name.trim();
    if (!trimmed) {
      setError("Please enter your name.");
      return;
    }
    if (joined) return;

    const emitJoin = () => {
      console.log("ðŸ”— Emitting joinGame:", trimmed);
      socket.emit("joinGame", trimmed);
      setJoined(true);
      setError("");
    };

    if (socket.connected) {
      emitJoin();
    } else {
      socket.once("connect", emitJoin);
    }
  };

  useEffect(() => {
    const handleGameStarted = ({ gameId, players, turn }) => {
      console.log("âœ… Game started with ID:", gameId, "Players:", players);
      const opponent = players.find((p) => p !== name) || "Bot";
      navigate(`/game/${gameId}`, {
        state: {
          username: name,
          gameId,
          opponent,
          turn,
        },
      });
    };

    socket.on("gameStarted", handleGameStarted);

    return () => {
      socket.off("gameStarted", handleGameStarted);
    };
  }, [name, navigate]);

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        minHeight: "100vh",
        backgroundColor: "#e0f7fa",
      }}
    >
      <h2 style={{ fontSize: "32px", color: "#0077b6", marginBottom: "20px" }}>
        Enter Your Name to Play
      </h2>
      <input
        type="text"
        placeholder="Your Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{
          padding: "12px",
          fontSize: "16px",
          borderRadius: "8px",
          border: "1px solid #ccc",
          marginBottom: "10px",
          width: "250px",
          textAlign: "center",
        }}
        disabled={joined}
      />
      {error && <p style={{ color: "red", marginBottom: "10px" }}>{error}</p>}
      <button
        onClick={joinGame}
        disabled={joined}
        style={{
          padding: "12px 25px",
          fontSize: "16px",
          backgroundColor: joined ? "#aaa" : "#0077b6",
          color: "#fff",
          border: "none",
          borderRadius: "8px",
          cursor: joined ? "not-allowed" : "pointer",
          fontWeight: "bold",
          transition: "background 0.3s",
        }}
      >
        {joined ? "Waiting for opponent..." : "Play Now"}
      </button>
    </div>
  );
};

export default Lobby;
