import React from "react";
import { useNavigate } from "react-router-dom";

const Landing = () => {
  const navigate = useNavigate();

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        minHeight: "100vh",
        backgroundColor: "#0077b6",
        color: "#fff",
        textAlign: "center",
      }}
    >
      <h1 style={{ fontSize: "48px", marginBottom: "20px" }}>Connect4</h1>
      <p style={{ fontSize: "20px", marginBottom: "40px" }}>
        Challenge your friends and climb the leaderboard!
      </p>
      <button
        onClick={() => navigate("/play")}
        style={{
          padding: "15px 30px",
          fontSize: "18px",
          backgroundColor: "#0096c7",
          color: "#fff",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
          marginBottom: "15px",
        }}
      >
        Play Now
      </button>
      <button
        onClick={() => navigate("/leaderboard")}
        style={{
          padding: "12px 25px",
          fontSize: "16px",
          backgroundColor: "#023e8a",
          color: "#fff",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
        }}
      >
        View Leaderboard
      </button>
    </div>
  );
};

export default Landing;
