import React, { useEffect, useState } from "react";
import socket from "../socket";

const ROWS = 6;
const COLS = 7;

const GameBoard = ({ username, gameId, turnProp, opponentProp, setInGame }) => {
  const [board, setBoard] = useState(
    Array.from({ length: ROWS }, () => Array(COLS).fill(null))
  );

  const [turn, setTurn] = useState(turnProp || username);
  const [opponent, setOpponent] = useState(opponentProp || "BotMaster");

  const makeMove = (col) => {
    if (turn !== username) return;
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    console.log("🔁 GameBoard mounted. Username:", username, "Opponent:", opponent);

    socket.on("moveMade", ({ board }) => {
      setBoard([...board]);
      setTurn((prev) => (prev === username ? opponent : username));
    });

    socket.on("gameOver", ({ winner, draw }) => {
      if (draw) {
        alert("It's a Draw!");
      } else {
        alert(winner === username ? "You Win!" : `${winner} Wins`);
      }
      if (setInGame) setInGame(false);
    });

    socket.on("playerDisconnected", ({ message }) => alert(message));
    socket.on("playerRejoined", ({ message }) => alert(message));

    return () => {
      socket.off("moveMade");
      socket.off("gameOver");
      socket.off("playerDisconnected");
      socket.off("playerRejoined");
    };
  }, [username, opponent, setInGame]);

  return (
    <div style={{ textAlign: "center", marginTop: "40px" }}>
      <h2>4 in a Row</h2>
      <h3>Turn: <span>{turn}</span></h3>
      <h4>Opponent: <span>{opponent}</span></h4>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${COLS}, 60px)`,
          gap: "8px",
          justifyContent: "center",
          marginTop: "20px",
        }}
      >
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`}
              onClick={() => makeMove(cIdx)}
              style={{
                width: "60px",
                height: "60px",
                backgroundColor:
                  cell === "X" ? "#d62828" : cell === "O" ? "#fcbf49" : "#e0e0e0",
                borderRadius: "50%",
                cursor: turn === username ? "pointer" : "not-allowed",
              }}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
