// src/components/Lobby.jsx
import React, { useState, useEffect } from "react";
import socket from "../socket";

const Lobby = ({ setUsername, setGameId, setInGame }) => {
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false); // ✅ Guard flag

  const joinGame = () => {
    if (name.trim() === "" || joined) return;

    socket.emit("join", name);
    setUsername(name);
    setJoined(true); // ✅ Prevent further joins
  };

  useEffect(() => {
    const handleGameStarted = (data) => {
      console.log("✅ Game started:", data);
      setGameId(data.gameId);
      setInGame(true);
    };

    socket.on("gameStarted", handleGameStarted);

    return () => {
      socket.off("gameStarted", handleGameStarted); // ✅ Clean up
    };
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "100px" }}>
      <h2>Enter your name to play</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{ padding: "8px", fontSize: "16px" }}
      />
      <br /><br />
      <button
        onClick={joinGame}
        style={{ padding: "10px 20px", fontSize: "16px", cursor: "pointer" }}
      >
        Join Game
      </button>
    </div>
  );
};

export default Lobby;
