// src/components/GameBoard.jsx
import React, { useEffect, useState } from "react";
import socket from "../socket";

const GameBoard = ({ username, gameId }) => {
  const [board, setBoard] = useState(Array(6).fill(Array(7).fill(null)));
  const [turn, setTurn] = useState(null);
  const [result, setResult] = useState("");

  const makeMove = (col) => {
    if (turn !== username || result) return;
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    socket.on("moveMade", ({ board }) => {
      setBoard([...board]);
    });

    socket.on("turnChange", ({ turn }) => {
      setTurn(turn);
    });

    socket.on("gameOver", ({ winner, draw }) => {
      if (draw) setResult("Draw!");
      else setResult(`${winner} wins!`);
    });

    socket.on("gameStarted", ({ turn }) => {
      setTurn(turn);
    });

    return () => {
      socket.off();
    };
  }, []);

  return (
    <div>
      <h3>{result ? result : `Turn: ${turn}`}</h3>
      <div style={{ display: "grid", gridTemplateColumns: "repeat(7, 50px)" }}>
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`}
              onClick={() => makeMove(cIdx)}
              style={{
                width: 50,
                height: 50,
                border: "1px solid #000",
                background: cell === "X" ? "red" : cell === "O" ? "yellow" : "white",
              }}
            ></div>
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
