// src/components/Landing.jsx

import React from "react";
import { useNavigate } from "react-router-dom";

const Landing = () => {
  const navigate = useNavigate();

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(135deg, #90e0ef, #00b4d8)",
      }}
    >
      <h1 style={{ fontSize: "48px", color: "#03045e", marginBottom: "20px" }}>
        Connect 4
      </h1>
      <button
        onClick={() => navigate("/name")}
        style={{
          padding: "14px 32px",
          fontSize: "18px",
          backgroundColor: "#0077b6",
          color: "#fff",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
          fontWeight: "bold",
          transition: "background 0.3s",
        }}
        onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#023e8a")}
        onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#0077b6")}
      >
        Play Now
      </button>
    </div>
  );
};

export default Landing;
