// src/components/Lobby.jsx

import React, { useState, useEffect } from "react";
import socket from "../socket";

const Lobby = ({ setUsername, setGameId, setInGame }) => {
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false); // ✅ Prevents duplicate join attempts

  const joinGame = () => {
    if (name.trim() === "" || joined) return;

    socket.emit("join", name);
    setUsername(name);
    setJoined(true); // ✅ Mark as joined
  };

  useEffect(() => {
    const handleGameStarted = ({ gameId }) => {
      console.log("✅ Game started with ID:", gameId);
      setGameId(gameId);
      setInGame(true);
    };

    socket.on("gameStarted", handleGameStarted);

    return () => {
      socket.off("gameStarted", handleGameStarted);
    };
  }, [setGameId, setInGame]);

  return (
    <div style={{ textAlign: "center", marginTop: "100px" }}>
      <h2>Enter your name to play</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{ padding: "10px", fontSize: "16px", width: "250px" }}
        disabled={joined}
      />
      <br /><br />
      <button
        onClick={joinGame}
        style={{
          padding: "10px 30px",
          fontSize: "16px",
          cursor: joined ? "not-allowed" : "pointer",
        }}
        disabled={joined}
      >
        {joined ? "Waiting for opponent..." : "Join Game"}
      </button>
    </div>
  );
};

export default Lobby;
