import React, { useState } from "react";
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import Landing from "./components/Landing";
import NameEntry from "./components/NameEntry";
import GameBoard from "./components/GameBoard";

function GameWrapper() {
  const location = useLocation();
  const [inGame, setInGame] = useState(true);

  // Extract username and optional turn/opponent from state
  const { username, turn, opponent } = location.state || {};

  // If no username (direct URL access), return to landing
  if (!username) {
    window.location.href = "/";
    return null;
  }

  return (
    <GameBoard
      username={username}
      gameId={location.pathname.split("/").pop()}
      turnProp={turn}
      opponentProp={opponent}
      setInGame={setInGame}
    />
  );
}

function App() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Landing />} />
        <Route path="/play" element={<NameEntry />} />
        <Route path="/game/:gameId" element={<GameWrapper />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
