import React, { useState, useEffect } from "react";
import Lobby from "./components/Lobby";
import GameBoard from "./components/GameBoard";
import Leaderboard from "./components/Leaderboard";
import socket from "./socket";

function App() {
  const [username, setUsername] = useState("");
  const [gameId, setGameId] = useState("");
  const [inGame, setInGame] = useState(false);
  const [gameBoardProps, setGameBoardProps] = useState(null);

  useEffect(() => {
    socket.on("gameStarted", ({ gameId, board, players, turn }) => {
      console.log("ðŸŽ® Received gameStarted in App:", { gameId, players, turn });
      setGameId(gameId);
      setInGame(true);

      const opponent = players.find(p => p !== username);
      setGameBoardProps({
        gameId,
        board,
        turn,
        players,
        opponent: opponent || "BotMaster",
      });
    });

    return () => {
      socket.off("gameStarted");
    };
  }, [username]);

  return (
    <div>
      {!inGame ? (
        <>
          <Lobby setUsername={setUsername} setGameId={setGameId} setInGame={setInGame} />
          <Leaderboard />
        </>
      ) : (
        <GameBoard
          username={username}
          gameId={gameBoardProps?.gameId}
          board={gameBoardProps?.board}
          turnProp={gameBoardProps?.turn}
          opponentProp={gameBoardProps?.opponent}
          setInGame={setInGame}
        />
      )}
    </div>
  );
}

export default App;
