// src/components/NameEntry.jsx

import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import socket from "../socket";

const NameEntry = () => {
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const navigate = useNavigate();

  const joinGame = () => {
    const trimmed = name.trim();
    if (!trimmed || joined) return;

    socket.emit("joinGame", trimmed);
    setJoined(true);
  };

  useEffect(() => {
    const handleGameStarted = ({ gameId }) => {
      navigate("/game", { state: { name } });
    };

    socket.on("gameStarted", handleGameStarted);
    return () => {
      socket.off("gameStarted", handleGameStarted);
    };
  }, [name, navigate]);

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#e0f7fa",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      <h2 style={{ fontSize: "28px", color: "#0077b6", marginBottom: "20px" }}>
        Enter Your Name to Play
      </h2>
      <input
        type="text"
        placeholder="Your Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        disabled={joined}
        style={{
          padding: "12px",
          fontSize: "16px",
          width: "80%",
          maxWidth: "300px",
          borderRadius: "6px",
          border: "2px solid #0077b6",
          marginBottom: "20px",
        }}
      />
      <button
        onClick={joinGame}
        disabled={joined}
        style={{
          padding: "12px 28px",
          fontSize: "16px",
          backgroundColor: joined ? "#aaa" : "#0077b6",
          color: "#fff",
          border: "none",
          borderRadius: "6px",
          cursor: joined ? "not-allowed" : "pointer",
          fontWeight: "bold",
        }}
      >
        {joined ? "Waiting for opponent..." : "Join Game"}
      </button>
    </div>
  );
};

export default NameEntry;
