// src/components/GameBoard.jsx

import React, { useEffect, useState } from "react";
import socket from "../socket";

const ROWS = 6;
const COLS = 7;

const GameBoard = ({ username, gameId, setInGame }) => {
  const [board, setBoard] = useState(
    Array.from({ length: ROWS }, () => Array(COLS).fill(null))
  );
  const [turn, setTurn] = useState(null);
  const [opponent, setOpponent] = useState(null);

  const makeMove = (col) => {
    if (turn !== username) return;
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    const handleGameStarted = ({ board, turn, players }) => {
      setBoard(board);
      setTurn(turn);
      const opp = players.find((p) => p !== username);
      setOpponent(opp);
    };

    const handleMoveMade = ({ board }) => {
      setBoard([...board]);
    };

    const handleTurnChange = ({ turn }) => {
      setTurn(turn);
    };

    const handleGameOver = ({ winner, draw }) => {
      if (draw) {
        alert("It's a Draw!");
      } else {
        alert(winner === username ? "You Win!" : `${winner} Wins`);
      }
      setInGame(false);
    };

    const handleDisconnect = ({ message }) => alert(message);
    const handleRejoin = ({ message }) => alert(message);

    socket.on("gameStarted", handleGameStarted);
    socket.on("moveMade", handleMoveMade);
    socket.on("turnChange", handleTurnChange);
    socket.on("gameOver", handleGameOver);
    socket.on("playerDisconnected", handleDisconnect);
    socket.on("playerRejoined", handleRejoin);

    return () => {
      socket.off("gameStarted", handleGameStarted);
      socket.off("moveMade", handleMoveMade);
      socket.off("turnChange", handleTurnChange);
      socket.off("gameOver", handleGameOver);
      socket.off("playerDisconnected", handleDisconnect);
      socket.off("playerRejoined", handleRejoin);
    };
  }, [username, setInGame]);

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>4 in a Row</h2>
      <h3>Turn: {turn || "Loading..."}</h3>
      <h4>Opponent: {opponent || "Waiting..."}</h4>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${COLS}, 50px)`,
          justifyContent: "center",
          gap: "5px",
        }}
      >
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`}
              onClick={() => makeMove(cIdx)}
              style={{
                width: 50,
                height: 50,
                backgroundColor:
                  cell === "X" ? "red" : cell === "O" ? "yellow" : "#eee",
                border: "1px solid #333",
                borderRadius: "50%",
                cursor: turn === username ? "pointer" : "not-allowed",
              }}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
