import React, { useEffect, useState } from "react";
import axios from "axios";

const Leaderboard = () => {
  const [data, setData] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const res = await axios.get("http://localhost:5000/leaderboard");
        setData(res.data);
      } catch (err) {
        console.error("Error fetching leaderboard:", err.message);
        setError("Could not load leaderboard. Try again later!");
      }
    };

    fetchLeaderboard();
  }, []);

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg shadow-md text-center">
      <h2 className="text-2xl font-bold mb-4 text-blue-700">üèÜ Leaderboard</h2>
      {error ? (
        <p className="text-red-500">{error}</p>
      ) : data.length === 0 ? (
        <p>No players yet. Be the first to win!</p>
      ) : (
        <ol className="text-left">
          {data.map((user, i) => (
            <li
              key={i}
              className={`p-2 rounded ${
                i === 0 ? "bg-yellow-100 font-bold" : ""
              }`}
            >
              {i + 1}. {user.winner} ‚Äî <strong>{user.wins}</strong>{" "}
              {user.wins > 1 ? "wins" : "win"}
            </li>
          ))}
        </ol>
      )}
    </div>
  );
};

export default Leaderboard;
