import React, { useState, useEffect } from "react";
import socket from "../socket";

const Lobby = ({ setUsername, setGameId, setInGame }) => {
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);

  const joinGame = () => {
    const normalizedName = name.trim().toLowerCase();
    if (!normalizedName || joined) return;

    const emitJoin = () => {
      console.log("ðŸ”— Emitting joinGame:", normalizedName);
      socket.emit("joinGame", normalizedName);
      setUsername(normalizedName);
      setJoined(true);
    };

    if (socket.connected) {
      emitJoin();
    } else {
      socket.once("connect", emitJoin);
    }
  };

  useEffect(() => {
    const handleGameStarted = ({ gameId }) => {
      console.log("âœ… Game started with ID:", gameId);
      setGameId(gameId);
      setInGame(true);
    };

    socket.on("gameStarted", handleGameStarted);

    return () => {
      socket.off("gameStarted", handleGameStarted);
    };
  }, [setGameId, setInGame]);

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        minHeight: "100vh",
        backgroundColor: "#e0f7fa",
      }}
    >
      <div
        style={{
          backgroundColor: "#ffffff",
          padding: "40px",
          borderRadius: "12px",
          textAlign: "center",
          boxShadow: "0 6px 16px rgba(0, 0, 0, 0.1)",
          maxWidth: "350px",
          width: "90%",
        }}
      >
        <h2 style={{ fontSize: "24px", color: "#0077b6", marginBottom: "20px" }}>
          Enter your name to play
        </h2>
        <input
          type="text"
          placeholder="Your name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{
            padding: "12px",
            fontSize: "16px",
            width: "80%",
            maxWidth: "250px",
            marginBottom: "20px",
            border: "2px solid #ccc",
            borderRadius: "6px",
            outline: "none",
          }}
          disabled={joined}
        />
        <br />
        <button
          onClick={joinGame}
          disabled={joined}
          style={{
            padding: "12px 30px",
            fontSize: "16px",
            backgroundColor: joined ? "#aaa" : "#0077b6",
            color: "#fff",
            border: "none",
            borderRadius: "6px",
            cursor: joined ? "not-allowed" : "pointer",
            fontWeight: "bold",
            transition: "background 0.3s",
          }}
          onMouseOver={(e) => {
            if (!joined) e.currentTarget.style.backgroundColor = "#005f8a";
          }}
          onMouseOut={(e) => {
            if (!joined) e.currentTarget.style.backgroundColor = "#0077b6";
          }}
        >
          {joined ? "Waiting for opponent..." : "Join Game"}
        </button>
      </div>
    </div>
  );
};

export default Lobby;
