import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import socket from "../socket";

const Lobby = () => {
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const joinGame = () => {
    const trimmed = name.trim();
    if (!trimmed) {
      setError("Please enter your name.");
      return;
    }
    if (joined) return;

    const cleanedName = trimmed; // keep original case

    const emitJoin = () => {
      console.log("ðŸ”— Emitting joinGame:", cleanedName);
      socket.emit("joinGame", cleanedName);
      setJoined(true);
      setError("");
    };

    if (socket.connected) {
      emitJoin();
    } else {
      socket.once("connect", emitJoin);
    }
  };

  useEffect(() => {
    const handleGameStarted = ({ gameId, players, turn }) => {
      console.log("âœ… Game started with ID:", gameId, "Players:", players, "Turn:", turn);

      const cleanedName = name.trim().toLowerCase();
      const opponent = players.find((p) => p !== cleanedName) || "BotMaster";

      navigate(`/game/${gameId}`, {
        state: {
          username: cleanedName,
          gameId,
          opponent,
          turn: turn.toLowerCase(), // also lowercased
        },
      });
    };

    socket.on("gameStarted", handleGameStarted);

    return () => {
      socket.off("gameStarted", handleGameStarted);
    };
  }, [name, navigate]);

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#0b1d1f",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        padding: "50px 20px",
        color: "#fff",
      }}
    >
      <div
        style={{
          backgroundColor: "#122c2f",
          borderRadius: "20px",
          padding: "40px",
          maxWidth: "500px",
          width: "100%",
          textAlign: "center",
        }}
      >
        <h2 style={{ fontSize: "32px", color: "#fff", marginBottom: "30px" }}>
          Enter Your Name to Play
        </h2>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{
            padding: "15px",
            fontSize: "16px",
            borderRadius: "20px",
            border: "none",
            marginBottom: "20px",
            width: "80%",
            textAlign: "center",
            backgroundColor: "#fff",
            boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)",
          }}
          disabled={joined}
        />
        {error && <p style={{ color: "#ff6b6b", marginBottom: "20px", fontSize: "16px" }}>{error}</p>}
        <button
          onClick={joinGame}
          disabled={joined}
          style={{
            padding: "12px 30px",
            fontSize: "18px",
            backgroundColor: joined ? "#555" : "#6ee7b7",
            color: joined ? "#fff" : "#000",
            border: "none",
            borderRadius: "20px",
            cursor: joined ? "not-allowed" : "pointer",
            fontWeight: "bold",
            transition: "background 0.3s",
          }}
        >
          {joined ? "Waiting for opponent..." : "Play Now"}
        </button>
      </div>
    </div>
  );
};

export default Lobby;
