import React, { useEffect, useState } from "react";
import socket from "../socket";

const ROWS = 6;
const COLS = 7;

const GameBoard = ({ username, gameId, turnProp, opponentProp, setInGame }) => {
  const [board, setBoard] = useState(Array.from({ length: ROWS }, () => Array(COLS).fill(null)));
  const [turn, setTurn] = useState(turnProp || null);
  const opponent = opponentProp || "BotMaster"; // ✅ removed unused setOpponent

  const makeMove = (col) => {
    if (turn !== username) return;
    socket.emit("makeMove", { gameId, column: col });
  };

  useEffect(() => {
    // Sync props after mount
    setTurn(turnProp);
  }, [turnProp]);

  useEffect(() => {
    console.log("🔁 GameBoard mounted. Username:", username, "Opponent:", opponent);

    socket.on("moveMade", ({ board }) => {
      setBoard([...board]);

      // Switch turn locally
      setTurn(prev => (prev === username ? opponent : username));
    });

    socket.on("gameOver", ({ winner, draw }) => {
      if (draw) {
        alert("It's a Draw!");
      } else {
        alert(winner === username ? "You Win!" : `${winner} Wins`);
      }
      setInGame(false);
    });

    socket.on("playerDisconnected", ({ message }) => alert(message));
    socket.on("playerRejoined", ({ message }) => alert(message));

    return () => {
      socket.off("moveMade");
      socket.off("gameOver");
      socket.off("playerDisconnected");
      socket.off("playerRejoined");
    };
  }, [username, opponent, setInGame]);

  return (
    <div
      style={{
        textAlign: "center",
        marginTop: "40px",
        backgroundColor: "#f0f4f8",
        minHeight: "100vh",
        padding: "20px"
      }}
    >
      <h2 style={{ fontSize: "32px", color: "#0077b6", marginBottom: "10px" }}>4 in a Row</h2>
      <h3 style={{ fontSize: "20px", margin: "5px 0" }}>
        Turn: <span style={{ color: "#023e8a" }}>{turn || "Loading..."}</span>
      </h3>
      <h4 style={{ fontSize: "18px", margin: "5px 0" }}>
        Opponent: <span style={{ color: "#023e8a" }}>{opponent || "Waiting..."}</span>
      </h4>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${COLS}, 60px)`, // ✅ fixed template literal
          justifyContent: "center",
          gap: "8px",
          backgroundColor: "#caf0f8",
          padding: "20px",
          borderRadius: "12px",
          boxShadow: "0 4px 12px rgba(0, 0, 0, 0.15)",
          marginTop: "20px"
        }}
      >
        {board.map((row, rIdx) =>
          row.map((cell, cIdx) => (
            <div
              key={`${rIdx}-${cIdx}`} // ✅ fixed template literal
              onClick={() => makeMove(cIdx)}
              style={{
                width: "60px",
                height: "60px",
                backgroundColor:
                  cell === "X" ? "#d62828" : cell === "O" ? "#fcbf49" : "#e0e0e0",
                border: "2px solid #333",
                borderRadius: "50%",
                cursor: turn === username ? "pointer" : "not-allowed",
                transition: "transform 0.2s",
              }}
              onMouseOver={(e) => {
                if (turn === username) e.currentTarget.style.transform = "scale(1.1)";
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.transform = "scale(1)";
              }}
            ></div>
          ))
        )}
      </div>
    </div>
  );
};

export default GameBoard;
